CREATE TABLE IF NOT EXISTS `product_review` (  `id` bigint(20) NOT NULL AUTO_INCREMENT,  `product_id` bigint(20) NOT NULL,  `is_active` tinyint(1) NOT NULL,  `customer_id` bigint(20) DEFAULT NULL,  `review` longtext,  `created` datetime(6) NOT NULL,  `rating` double NOT NULL,  `updated` datetime(6) NOT NULL,  `name` varchar(255) DEFAULT NULL,  `email` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `FKkaqmhakwt05p3n0px81b9pdya` (`product_id`),  KEY `FKq4rtmlnndigdh17ycq1lc8c7u` (`customer_id`),  CONSTRAINT `FKq4rtmlnndigdh17ycq1lc8c7u` FOREIGN KEY (`customer_id`) REFERENCES `customer` (`id`),  CONSTRAINT `FKkaqmhakwt05p3n0px81b9pdya` FOREIGN KEY (`product_id`) REFERENCES `product` (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;